mapper.select:
SELECT 

 FROM ic_item WHERE data_state =1
 
 and  MALL_CAT_ID = #{mallCatId,jdbcType=DECIMAL}
 
 
 and ITEM_CODE = #{itemCode,jdbcType=DECIMAL}
 
 
  and ITEM_TYPE = #{itemType,jdbcType=DECIMAL}
 
 
  and ITEM_NAME = #{itemName,jdbcType=VARCHAR}
 
 
  and ITEM_MARKET_PRICE = #{itemMarketPrice,jdbcType=DECIMAL}
 
 
  and ITEM_SALE_PRICE = #{itemSalePrice,jdbcType=DECIMAL}
 
 
 and  ITEM_SALE_NUM = #{itemSaleNum,jdbcType=DECIMAL}
 
 
  and ITEM_NUM = #{itemNum,jdbcType=DECIMAL}
 
 
  and ITEM_VALID_START = #{itemValidStart,jdbcType=TIMESTAMP}
 
 
  and ITEM_VALID_END = #{itemValidEnd,jdbcType=TIMESTAMP}
 
 
  and ITEM_APPR_STATE = #{itemApprState,jdbcType=DECIMAL}
 
 
  and ITEM_UD_STATE = #{itemUdState,jdbcType=DECIMAL}
 
 
  and ITEM_SCHDLON_STATE = #{itemSchdlonState,jdbcType=DECIMAL}
