mapper.select:

    SELECT 
    
    FROM tc_goods WHERE data_state=1
    
     and ORDER_ID = #{orderId,jdbcType=DECIMAL}
    
    
      and ITEM_CHK_CODE = #{itemChkCode,jdbcType=VARCHAR}
    
    
     and  MCHT_ID = #{mchtId,jdbcType=DECIMAL}
    
    
     and MCHT_NAME = #{mchtName,jdbcType=VARCHAR}
    
    
    and  ITEM_ID = #{itemId,jdbcType=DECIMAL}
    
    
     and ITEM_TYPE = #{itemType,jdbcType=DECIMAL}
    
    
     and ITEM_CODE = #{itemCode,jdbcType=DECIMAL}
    
    
     and ITEM_NAME = #{itemName,jdbcType=VARCHAR}
    
    
     and ITEM_ACT_PRICE = #{itemActPrice,jdbcType=DECIMAL}
    
    
    and  GOODS_DEAL_FEE = #{goodsDealFee,jdbcType=DECIMAL}
    
    
    and  RTNISTD_STATE = #{rtnistdState,jdbcType=DECIMAL}
    
    
     and RTNISTD_RSN_TYPE = #{rtnistdRsnType,jdbcType=DECIMAL}
    
    
     and RTNISTD_REASON = #{rtnistdReason,jdbcType=VARCHAR}
    
    
    and  GOODS_RFD_FEE = #{goodsRfdFee,jdbcType=DECIMAL}
    
    
     and USER_ID = #{userId,jdbcType=DECIMAL}
    
    
     and USER_NAME = #{userName,jdbcType=VARCHAR}
    
    
     and USER_NICKNAME = #{userNickname,jdbcType=VARCHAR}
    
    
     and LOGIN_NAME = #{loginName,jdbcType=VARCHAR}
    
    
     and USER_PHONENO = #{userPhoneno,jdbcType=VARCHAR}
    
    
     and ITEM_VALID_START = #{itemValidStart,jdbcType=TIMESTAMP}
    
    
     and ITEM_VALID_END = #{itemValidEnd,jdbcType=TIMESTAMP}
    
    
     and USE_TIME = #{useTime,jdbcType=TIMESTAMP}
    
    
    and  USE_RESULT = #{useResult,jdbcType=DECIMAL}
    
    
    and  SHOP_ID = #{shopId,jdbcType=DECIMAL}
    
    
     and SHOP_SHORT_NAME = #{shopShortName,jdbcType=VARCHAR}
    
    
     and SHOP_NAME = #{shopName,jdbcType=VARCHAR}
    
    
     and EXP_STATE = #{expState,jdbcType=DECIMAL}
    
    
     and EXP_TIME = #{expTime,jdbcType=TIMESTAMP}
    
    
     and CHECK_STATE = #{checkState,jdbcType=DECIMAL}
    
    
    and  SETTLEMENT_DATE = #{settlementDate,jdbcType=TIMESTAMP}
    
    
     and DATA_STATE = #{dataState,jdbcType=DECIMAL}
    
    
     and CREATOR = #{creator,jdbcType=DECIMAL}
    
    
     and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}
    
    
     and MODIFYER = #{modifyer,jdbcType=DECIMAL}
    
    
     and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}

SELECT 
 
FROM tc_goods 
WHERE item_code = #{itemCode} 
OR item_name = #{itemName}
